import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { insertWhisperSchema, insertReplySchema, insertMoodTagSchema, insertTopicTagSchema, insertThemeSchema, insertFlagSchema, insertVoteSchema } from "@shared/schema";
import { z } from "zod";
import rateLimit from "express-rate-limit";

// Rate limiters
const whisperLimiter = rateLimit({
  windowMs: 60 * 60 * 1000, // 1 hour
  max: 5, // 5 whispers per hour
  message: { message: "Too many whispers. Please wait before posting again." },
  standardHeaders: true,
  legacyHeaders: false,
});

const replyLimiter = rateLimit({
  windowMs: 15 * 60 * 1000, // 15 minutes
  max: 10, // 10 replies per 15 minutes
  message: { message: "Too many replies. Please wait before replying again." },
  standardHeaders: true,
  legacyHeaders: false,
});

const voteLimiter = rateLimit({
  windowMs: 1 * 60 * 1000, // 1 minute
  max: 30, // 30 votes per minute
  message: { message: "Too many votes. Please slow down." },
  standardHeaders: true,
  legacyHeaders: false,
});

// Admin middleware
const isAdmin = async (req: any, res: any, next: any) => {
  try {
    const userId = req.user?.claims?.sub;
    if (!userId) {
      return res.status(401).json({ message: "Unauthorized" });
    }

    const user = await storage.getUser(userId);
    if (!user?.isAdmin) {
      return res.status(403).json({ message: "Admin access required" });
    }

    next();
  } catch (error) {
    console.error("Admin check error:", error);
    res.status(500).json({ message: "Internal server error" });
  }
};

function getClientIP(req: any): string {
  return req.headers['x-forwarded-for']?.split(',')[0] || 
         req.connection?.remoteAddress || 
         req.socket?.remoteAddress || 
         req.ip || 
         '127.0.0.1';
}

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Seed initial data
  try {
    const moodTags = await storage.getMoodTags();
    if (moodTags.length === 0) {
      const defaultMoods = [
        { name: "Hope", description: "Feelings of optimism and expectation" },
        { name: "Regret", description: "Feelings of sadness or disappointment" },
        { name: "Fear", description: "Feelings of anxiety or worry" },
        { name: "Joy", description: "Feelings of happiness and delight" },
        { name: "Sadness", description: "Feelings of sorrow and melancholy" },
        { name: "Love", description: "Feelings of affection and care" },
        { name: "Anger", description: "Feelings of frustration and rage" },
      ];

      for (const mood of defaultMoods) {
        await storage.createMoodTag(mood);
      }
    }

    const topicTags = await storage.getTopicTags();
    if (topicTags.length === 0) {
      const defaultTopics = [
        { name: "Relationships", description: "Love, friendship, family dynamics" },
        { name: "Work", description: "Career, job experiences, workplace" },
        { name: "Family", description: "Family relationships and experiences" },
        { name: "Dreams", description: "Aspirations, goals, and ambitions" },
        { name: "Mental Health", description: "Emotional wellbeing and mental health" },
      ];

      for (const topic of defaultTopics) {
        await storage.createTopicTag(topic);
      }
    }
  } catch (error) {
    console.error("Error seeding data:", error);
  }

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Public API routes

  // Get whispers with filtering
  app.get('/api/whispers', async (req, res) => {
    try {
      const { mood, topic, theme, sort, search, page = "1", limit = "12" } = req.query;

      const filters = {
        moodTagId: mood as string,
        topicTagId: topic as string,
        themeId: theme as string,
        sort: sort as 'recent' | 'popular' | 'discussed',
        search: search as string,
        limit: parseInt(limit as string),
        offset: (parseInt(page as string) - 1) * parseInt(limit as string),
      };

      const whispers = await storage.getWhispers(filters);
      res.json(whispers);
    } catch (error) {
      console.error("Error fetching whispers:", error);
      res.status(500).json({ message: "Failed to fetch whispers" });
    }
  });

  // Get single whisper with replies
  app.get('/api/whispers/:id', async (req, res) => {
    try {
      const whisper = await storage.getWhisperById(req.params.id);
      if (!whisper) {
        return res.status(404).json({ message: "Whisper not found" });
      }
      res.json(whisper);
    } catch (error) {
      console.error("Error fetching whisper:", error);
      res.status(500).json({ message: "Failed to fetch whisper" });
    }
  });

  // Create whisper
  app.post('/api/whispers', whisperLimiter, async (req, res) => {
    try {
      const data = insertWhisperSchema.parse(req.body);
      const whisper = await storage.createWhisper(data, getClientIP(req));
      res.status(201).json(whisper);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Validation error", errors: error.errors });
      }
      console.error("Error creating whisper:", error);
      res.status(500).json({ message: "Failed to create whisper" });
    }
  });

  // Create reply
  app.post('/api/whispers/:id/replies', replyLimiter, async (req, res) => {
    try {
      const data = insertReplySchema.parse({
        ...req.body,
        whisperId: req.params.id,
      });

      const reply = await storage.createReply(data, getClientIP(req));
      res.status(201).json(reply);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Validation error", errors: error.errors });
      }
      console.error("Error creating reply:", error);
      res.status(500).json({ message: "Failed to create reply" });
    }
  });

  // Vote on content
  app.post('/api/vote', voteLimiter, async (req, res) => {
    try {
      const { contentType, contentId, voteType } = req.body;
      const ipAddress = getClientIP(req);

      if (!['whisper', 'reply'].includes(contentType)) {
        return res.status(400).json({ message: "Invalid content type" });
      }

      if (!['up', 'down'].includes(voteType)) {
        return res.status(400).json({ message: "Invalid vote type" });
      }

      // Check if user already voted
      const existingVote = await storage.hasVoted(contentType, contentId, ipAddress);
      if (existingVote) {
        if (existingVote.voteType === voteType) {
          // Remove vote if same type
          await storage.removeVote(contentType, contentId, ipAddress);
        } else {
          // Remove old vote and add new one
          await storage.removeVote(contentType, contentId, ipAddress);
          await storage.recordVote({ contentType, contentId, voteType }, ipAddress);
        }
      } else {
        // Record new vote
        await storage.recordVote({ contentType, contentId, voteType }, ipAddress);
      }

      // Update vote counts
      // This is a simplified approach - in production you'd want to aggregate votes more efficiently
      res.json({ message: "Vote recorded" });
    } catch (error) {
      console.error("Error recording vote:", error);
      res.status(500).json({ message: "Failed to record vote" });
    }
  });

  // Report content
  app.post('/api/report', async (req, res) => {
    try {
      const data = insertFlagSchema.parse(req.body);
      const flag = await storage.createFlag(data, getClientIP(req));

      // Flag the content
      if (data.contentType === 'whisper') {
        await storage.flagWhisper(data.contentId);
      } else {
        await storage.flagReply(data.contentId);
      }

      res.status(201).json({ message: "Content reported successfully" });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Validation error", errors: error.errors });
      }
      console.error("Error reporting content:", error);
      res.status(500).json({ message: "Failed to report content" });
    }
  });

  // Get tags
  app.get('/api/tags', async (req, res) => {
    try {
      const [moodTags, topicTags] = await Promise.all([
        storage.getMoodTags(),
        storage.getTopicTags()
      ]);

      res.json({ moodTags, topicTags });
    } catch (error) {
      console.error("Error fetching tags:", error);
      res.status(500).json({ message: "Failed to fetch tags" });
    }
  });

  // Get current theme
  app.get('/api/themes/current', async (req, res) => {
    try {
      const theme = await storage.getCurrentTheme();
      res.json(theme);
    } catch (error) {
      console.error("Error fetching current theme:", error);
      res.status(500).json({ message: "Failed to fetch current theme" });
    }
  });

  // Get all themes
  app.get('/api/themes', async (req, res) => {
    try {
      const themes = await storage.getThemes();
      res.json(themes);
    } catch (error) {
      console.error("Error fetching themes:", error);
      res.status(500).json({ message: "Failed to fetch themes" });
    }
  });

  // Admin routes
  app.get('/api/admin/stats', isAuthenticated, isAdmin, async (req: any, res) => {
    try {
      const stats = await storage.getStats();
      res.json(stats);
    } catch (error) {
      console.error("Error fetching admin stats:", error);
      res.status(500).json({ message: "Failed to fetch stats" });
    }
  });

  app.get('/api/admin/flagged', isAuthenticated, isAdmin, async (req: any, res) => {
    try {
      const flaggedContent = await storage.getFlaggedContent();
      res.json(flaggedContent);
    } catch (error) {
      console.error("Error fetching flagged content:", error);
      res.status(500).json({ message: "Failed to fetch flagged content" });
    }
  });

  app.post('/api/admin/content/:type/:id/approve', isAuthenticated, isAdmin, async (req: any, res) => {
    try {
      const { type, id } = req.params;

      if (type === 'whisper') {
        // Unflag whisper (you'd implement this method in storage)
        // await storage.unflagWhisper(id);
      } else if (type === 'reply') {
        // await storage.unflagReply(id);
      }

      await storage.logAdminAction({
        adminUserId: req.user.claims.sub,
        actionType: 'approve_content',
        targetType: type,
        targetId: id,
      });

      res.json({ message: "Content approved" });
    } catch (error) {
      console.error("Error approving content:", error);
      res.status(500).json({ message: "Failed to approve content" });
    }
  });

  app.delete('/api/admin/content/:type/:id', isAuthenticated, isAdmin, async (req: any, res) => {
    try {
      const { type, id } = req.params;

      if (type === 'whisper') {
        await storage.deleteWhisper(id);
      } else if (type === 'reply') {
        await storage.deleteReply(id);
      }

      await storage.logAdminAction({
        adminUserId: req.user.claims.sub,
        actionType: 'delete_content',
        targetType: type,
        targetId: id,
      });

      res.json({ message: "Content deleted" });
    } catch (error) {
      console.error("Error deleting content:", error);
      res.status(500).json({ message: "Failed to delete content" });
    }
  });

  app.post('/api/admin/tags/mood', isAuthenticated, isAdmin, async (req: any, res) => {
    try {
      const data = insertMoodTagSchema.parse(req.body);
      const tag = await storage.createMoodTag(data);

      await storage.logAdminAction({
        adminUserId: req.user.claims.sub,
        actionType: 'create_mood_tag',
        details: JSON.stringify(data),
      });

      res.status(201).json(tag);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Validation error", errors: error.errors });
      }
      console.error("Error creating mood tag:", error);
      res.status(500).json({ message: "Failed to create mood tag" });
    }
  });

  app.post('/api/admin/tags/topic', isAuthenticated, isAdmin, async (req: any, res) => {
    try {
      const data = insertTopicTagSchema.parse(req.body);
      const tag = await storage.createTopicTag(data);

      await storage.logAdminAction({
        adminUserId: req.user.claims.sub,
        actionType: 'create_topic_tag',
        details: JSON.stringify(data),
      });

      res.status(201).json(tag);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Validation error", errors: error.errors });
      }
      console.error("Error creating topic tag:", error);
      res.status(500).json({ message: "Failed to create topic tag" });
    }
  });

  app.post('/api/admin/themes', isAuthenticated, isAdmin, async (req: any, res) => {
    try {
      const data = insertThemeSchema.parse(req.body);
      const theme = await storage.createTheme(data);

      await storage.logAdminAction({
        adminUserId: req.user.claims.sub,
        actionType: 'create_theme',
        details: JSON.stringify(data),
      });

      res.status(201).json(theme);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Validation error", errors: error.errors });
      }
      console.error("Error creating theme:", error);
      res.status(500).json({ message: "Failed to create theme" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  whispers,
  replies,
  moodTags,
  topicTags,
  themes,
  flags,
  votes,
  adminActions,
  type User,
  type UpsertUser,
  type Whisper,
  type WhisperWithRelations,
  type InsertWhisper,
  type Reply,
  type InsertReply,
  type MoodTag,
  type TopicTag,
  type Theme,
  type Flag,
  type Vote,
  type AdminAction,
  type InsertMoodTag,
  type InsertTopicTag,
  type InsertTheme,
  type InsertFlag,
  type InsertVote,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, and, or, sql, count, asc } from "drizzle-orm";
import { createHash } from "crypto";

export interface IStorage {
  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Whisper operations
  getWhispers(filters?: {
    moodTagId?: string;
    topicTagId?: string;
    themeId?: string;
    sort?: 'recent' | 'popular' | 'discussed';
    search?: string;
    limit?: number;
    offset?: number;
  }): Promise<WhisperWithRelations[]>;
  getWhisperById(id: string): Promise<WhisperWithRelations | undefined>;
  createWhisper(whisper: InsertWhisper, ipAddress: string): Promise<Whisper>;
  updateWhisperVotes(id: string, upvotes: number, downvotes: number): Promise<void>;
  flagWhisper(id: string): Promise<void>;
  deleteWhisper(id: string): Promise<void>;

  // Reply operations
  getRepliesByWhisperId(whisperId: string): Promise<Reply[]>;
  createReply(reply: InsertReply, ipAddress: string): Promise<Reply>;
  updateReplyVotes(id: string, upvotes: number, downvotes: number): Promise<void>;
  flagReply(id: string): Promise<void>;
  deleteReply(id: string): Promise<void>;

  // Tag operations
  getMoodTags(): Promise<MoodTag[]>;
  getTopicTags(): Promise<TopicTag[]>;
  createMoodTag(tag: InsertMoodTag): Promise<MoodTag>;
  createTopicTag(tag: InsertTopicTag): Promise<MoodTag>;
  deleteMoodTag(id: string): Promise<void>;
  deleteTopicTag(id: string): Promise<void>;

  // Theme operations
  getThemes(): Promise<Theme[]>;
  getCurrentTheme(): Promise<Theme | undefined>;
  createTheme(theme: InsertTheme): Promise<Theme>;
  updateTheme(id: string, theme: Partial<InsertTheme>): Promise<void>;
  deleteTheme(id: string): Promise<void>;

  // Voting operations
  hasVoted(contentType: string, contentId: string, ipAddress: string): Promise<Vote | undefined>;
  recordVote(vote: InsertVote, ipAddress: string): Promise<Vote>;
  removeVote(contentType: string, contentId: string, ipAddress: string): Promise<void>;

  // Flag operations
  createFlag(flag: InsertFlag, ipAddress: string): Promise<Flag>;
  getFlaggedContent(): Promise<(Flag & { whisper?: Whisper; reply?: Reply })[]>;

  // Admin operations
  getStats(): Promise<{
    totalWhispers: number;
    totalReplies: number;
    flaggedContent: number;
    activeThemes: number;
    todayWhispers: number;
  }>;
  logAdminAction(action: Omit<AdminAction, 'id' | 'createdAt'>): Promise<AdminAction>;
}

function hashIP(ip: string): string {
  return createHash('sha256').update(ip + (process.env.IP_SALT || 'default-salt')).digest('hex');
}

export class DatabaseStorage implements IStorage {
  // User operations (required for Replit Auth)
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // Whisper operations
  async getWhispers(filters?: {
    moodTagId?: string;
    topicTagId?: string;
    themeId?: string;
    sort?: 'recent' | 'popular' | 'discussed';
    search?: string;
    limit?: number;
    offset?: number;
  }): Promise<WhisperWithRelations[]> {
    let query = db
      .select({
        whisper: whispers,
        moodTag: moodTags,
        topicTag: topicTags,
        theme: themes,
        replyCount: sql<number>`cast(count(${replies.id}) as int)`.as('reply_count'),
      })
      .from(whispers)
      .leftJoin(moodTags, eq(whispers.moodTagId, moodTags.id))
      .leftJoin(topicTags, eq(whispers.topicTagId, topicTags.id))
      .leftJoin(themes, eq(whispers.themeId, themes.id))
      .leftJoin(replies, eq(whispers.id, replies.whisperId))
      .where(and(
        eq(whispers.buried, false),
        eq(whispers.flagged, false)
      ))
      .groupBy(whispers.id, moodTags.id, topicTags.id, themes.id);

    // Apply filters
    const conditions = [eq(whispers.buried, false), eq(whispers.flagged, false)];

    if (filters?.moodTagId) {
      conditions.push(eq(whispers.moodTagId, filters.moodTagId));
    }

    if (filters?.topicTagId) {
      conditions.push(eq(whispers.topicTagId, filters.topicTagId));
    }

    if (filters?.themeId) {
      conditions.push(eq(whispers.themeId, filters.themeId));
    }

    if (filters?.search) {
      conditions.push(sql`${whispers.text} ILIKE ${'%' + filters.search + '%'}`);
    }

    query = query.where(and(...conditions));

    // Apply sorting
    if (filters?.sort === 'popular') {
      query = query.orderBy(desc(sql`${whispers.upvotes} - ${whispers.downvotes}`));
    } else if (filters?.sort === 'discussed') {
      query = query.orderBy(desc(sql`count(${replies.id})`));
    } else {
      query = query.orderBy(desc(whispers.createdAt));
    }

    // Apply pagination
    if (filters?.limit) {
      query = query.limit(filters.limit);
    }
    if (filters?.offset) {
      query = query.offset(filters.offset);
    }

    const results = await query;

    return results.map(result => ({
      ...result.whisper,
      moodTag: result.moodTag || undefined,
      topicTag: result.topicTag || undefined,
      theme: result.theme || undefined,
      replies: [], // Will be populated separately if needed
    }));
  }

  async getWhisperById(id: string): Promise<WhisperWithRelations | undefined> {
    const [result] = await db
      .select({
        whisper: whispers,
        moodTag: moodTags,
        topicTag: topicTags,
        theme: themes,
      })
      .from(whispers)
      .leftJoin(moodTags, eq(whispers.moodTagId, moodTags.id))
      .leftJoin(topicTags, eq(whispers.topicTagId, topicTags.id))
      .leftJoin(themes, eq(whispers.themeId, themes.id))
      .where(eq(whispers.id, id));

    if (!result) return undefined;

    const whisperReplies = await this.getRepliesByWhisperId(id);

    return {
      ...result.whisper,
      moodTag: result.moodTag || undefined,
      topicTag: result.topicTag || undefined,
      theme: result.theme || undefined,
      replies: whisperReplies,
    };
  }

  async createWhisper(whisper: InsertWhisper, ipAddress: string): Promise<Whisper> {
    const [newWhisper] = await db
      .insert(whispers)
      .values({
        ...whisper,
        ipHash: hashIP(ipAddress),
      })
      .returning();
    return newWhisper;
  }

  async updateWhisperVotes(id: string, upvotes: number, downvotes: number): Promise<void> {
    await db
      .update(whispers)
      .set({ 
        upvotes, 
        downvotes,
        buried: downvotes >= 10 // Bury if too many downvotes
      })
      .where(eq(whispers.id, id));
  }

  async flagWhisper(id: string): Promise<void> {
    await db
      .update(whispers)
      .set({ flagged: true })
      .where(eq(whispers.id, id));
  }

  async deleteWhisper(id: string): Promise<void> {
    await db.delete(whispers).where(eq(whispers.id, id));
  }

  // Reply operations
  async getRepliesByWhisperId(whisperId: string): Promise<Reply[]> {
    return await db
      .select()
      .from(replies)
      .where(and(
        eq(replies.whisperId, whisperId),
        eq(replies.buried, false),
        eq(replies.flagged, false)
      ))
      .orderBy(asc(replies.createdAt));
  }

  async createReply(reply: InsertReply, ipAddress: string): Promise<Reply> {
    const [newReply] = await db
      .insert(replies)
      .values({
        ...reply,
        ipHash: hashIP(ipAddress),
      })
      .returning();
    return newReply;
  }

  async updateReplyVotes(id: string, upvotes: number, downvotes: number): Promise<void> {
    await db
      .update(replies)
      .set({ 
        upvotes, 
        downvotes,
        buried: downvotes >= 10 // Bury if too many downvotes
      })
      .where(eq(replies.id, id));
  }

  async flagReply(id: string): Promise<void> {
    await db
      .update(replies)
      .set({ flagged: true })
      .where(eq(replies.id, id));
  }

  async deleteReply(id: string): Promise<void> {
    await db.delete(replies).where(eq(replies.id, id));
  }

  // Tag operations
  async getMoodTags(): Promise<MoodTag[]> {
    return await db.select().from(moodTags).orderBy(asc(moodTags.name));
  }

  async getTopicTags(): Promise<TopicTag[]> {
    return await db.select().from(topicTags).orderBy(asc(topicTags.name));
  }

  async createMoodTag(tag: InsertMoodTag): Promise<MoodTag> {
    const [newTag] = await db.insert(moodTags).values(tag).returning();
    return newTag;
  }

  async createTopicTag(tag: InsertTopicTag): Promise<MoodTag> {
    const [newTag] = await db.insert(topicTags).values(tag).returning();
    return newTag;
  }

  async deleteMoodTag(id: string): Promise<void> {
    await db.delete(moodTags).where(eq(moodTags.id, id));
  }

  async deleteTopicTag(id: string): Promise<void> {
    await db.delete(topicTags).where(eq(topicTags.id, id));
  }

  // Theme operations
  async getThemes(): Promise<Theme[]> {
    return await db.select().from(themes).orderBy(desc(themes.startDate));
  }

  async getCurrentTheme(): Promise<Theme | undefined> {
    const now = new Date();
    const [theme] = await db
      .select()
      .from(themes)
      .where(and(
        eq(themes.isActive, true),
        sql`${themes.startDate} <= ${now}`,
        sql`${themes.endDate} >= ${now}`
      ))
      .orderBy(desc(themes.startDate))
      .limit(1);
    return theme;
  }

  async createTheme(theme: InsertTheme): Promise<Theme> {
    const [newTheme] = await db.insert(themes).values(theme).returning();
    return newTheme;
  }

  async updateTheme(id: string, theme: Partial<InsertTheme>): Promise<void> {
    await db.update(themes).set(theme).where(eq(themes.id, id));
  }

  async deleteTheme(id: string): Promise<void> {
    await db.delete(themes).where(eq(themes.id, id));
  }

  // Voting operations
  async hasVoted(contentType: string, contentId: string, ipAddress: string): Promise<Vote | undefined> {
    const [vote] = await db
      .select()
      .from(votes)
      .where(and(
        eq(votes.contentType, contentType),
        eq(votes.contentId, contentId),
        eq(votes.ipHash, hashIP(ipAddress))
      ));
    return vote;
  }

  async recordVote(vote: InsertVote, ipAddress: string): Promise<Vote> {
    const [newVote] = await db
      .insert(votes)
      .values({
        ...vote,
        ipHash: hashIP(ipAddress),
      })
      .returning();
    return newVote;
  }

  async removeVote(contentType: string, contentId: string, ipAddress: string): Promise<void> {
    await db
      .delete(votes)
      .where(and(
        eq(votes.contentType, contentType),
        eq(votes.contentId, contentId),
        eq(votes.ipHash, hashIP(ipAddress))
      ));
  }

  // Flag operations
  async createFlag(flag: InsertFlag, ipAddress: string): Promise<Flag> {
    const [newFlag] = await db
      .insert(flags)
      .values({
        ...flag,
        ipHash: hashIP(ipAddress),
      })
      .returning();
    return newFlag;
  }

  async getFlaggedContent(): Promise<(Flag & { whisper?: Whisper; reply?: Reply })[]> {
    const flaggedWhispers = await db
      .select({
        flag: flags,
        whisper: whispers,
      })
      .from(flags)
      .leftJoin(whispers, and(
        eq(flags.contentType, 'whisper'),
        eq(flags.contentId, whispers.id)
      ))
      .where(eq(flags.contentType, 'whisper'));

    const flaggedReplies = await db
      .select({
        flag: flags,
        reply: replies,
      })
      .from(flags)
      .leftJoin(replies, and(
        eq(flags.contentType, 'reply'),
        eq(flags.contentId, replies.id)
      ))
      .where(eq(flags.contentType, 'reply'));

    return [
      ...flaggedWhispers.map(f => ({ ...f.flag, whisper: f.whisper || undefined })),
      ...flaggedReplies.map(f => ({ ...f.flag, reply: f.reply || undefined })),
    ];
  }

  // Admin operations
  async getStats(): Promise<{
    totalWhispers: number;
    totalReplies: number;
    flaggedContent: number;
    activeThemes: number;
    todayWhispers: number;
  }> {
    const [whisperCount] = await db.select({ count: count() }).from(whispers);
    const [replyCount] = await db.select({ count: count() }).from(replies);
    const [flagCount] = await db.select({ count: count() }).from(flags);
    const [themeCount] = await db.select({ count: count() }).from(themes).where(eq(themes.isActive, true));

    const today = new Date();
    today.setHours(0, 0, 0, 0);
    const [todayCount] = await db
      .select({ count: count() })
      .from(whispers)
      .where(sql`${whispers.createdAt} >= ${today}`);

    return {
      totalWhispers: whisperCount.count,
      totalReplies: replyCount.count,
      flaggedContent: flagCount.count,
      activeThemes: themeCount.count,
      todayWhispers: todayCount.count,
    };
  }

  async logAdminAction(action: Omit<AdminAction, 'id' | 'createdAt'>): Promise<AdminAction> {
    const [newAction] = await db
      .insert(adminActions)
      .values(action)
      .returning();
    return newAction;
  }
}

export const storage = new DatabaseStorage();

